    useEffect(() => {



        axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=${API}&language=en-US`)
            .then(res => {
                setMovie(res.data);
            })
            .catch(err => {
                console.log(err);
                setMovie({});
            })

    })

    const [movie, setMovie] = useState({});
