import { useState } from 'react';
import Layout from '../components/Layout';
import { API } from '../config/keys';
import Movies from '../components/movies/Movies';

const UpcomingMovies = (props) => {

    const [search, setSearch] = useState('');
    const [popular, setPopular] = useState('');
    //console.log(props.upcoming)

    const onChange = e => {
        setSearch(e.target.value);

        if (search.length > 0) {

            axios.get(`https://api.themoviedb.org/3/search/movie?api_key=${API}&language=en-US&page=1&include_adult=false&query=${search}`)
                .then(res => {
                    setPopular(res.data);
                })
                .catch(err => {
                    setPopular('');
                });

        } else {
            axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=${API}&language=en-US&page=1`)
                .then(res => {
                    setPopular(res.data);
                })
                .catch(err => {
                    setPopular('');
                })
        }

    }

    return (
        <Layout>
            <div>
                <h3 className="type-heading">Upcoming Movies</h3>
                <input style={{ margin: '10px' }} className="search-input" type="text" placeholder="Search" value={search} onChange={onChange} />
                {popular !== '' ? (
                    <Movies movies={popular.results}></Movies>
                ) : (
                        <Movies movies={props.upcoming}></Movies>
                    )}
            </div>
        </Layout>
    )

}

UpcomingMovies.getInitialProps = async function () {
    const res = await fetch(`https://api.themoviedb.org/3/movie/upcoming?api_key=${API}&language=en-US&page=1`);
    const upcoming = await res.json();

    return {
        upcoming: upcoming.results
    }
}

export default UpcomingMovies;
