import { useState } from 'react';
import { API } from '../config/keys';
import Layout from '../components/Layout';
import Movies from '../components/movies/Movies';
import axios from 'axios';

const App = (props) => {

    const [search, setSearch] = useState('');
    const [popular, setPopular] = useState('');
    //console.log(props.popular)

    const onChange = e => {
        setSearch(e.target.value);

        if (search.length > 0) {

            axios.get(`https://api.themoviedb.org/3/search/movie?api_key=${API}&language=en-US&page=1&include_adult=false&query=${search}`)
                .then(res => {

                    // const sorted = res.data.results.sort(function (a, b) {
                    //     return a.vote_average - b.vote_average;
                    // })

                    setPopular(res.data);
                })
                .catch(err => {
                    setPopular('');
                });

        }

    }

    return (
        <Layout>
            <div>
                <h3 className="type-heading">Most Popular Movies</h3>
                <input style={{ margin: '10px' }} className="search-input" type="text" placeholder="Search" value={search} onChange={onChange} />
                {popular !== '' ? (
                    <Movies movies={popular.results}></Movies>
                ) : (
                        <Movies movies={props.popular}></Movies>
                    )}

            </div>
        </Layout>
    )
}

App.getInitialProps = async function () {
    const res = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${API}&language=en-US&page=1`);
    const popular = await res.json();

    return {
        popular: popular.results
    }
}

export default App;
