import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import { API } from '../../config/keys';
import axios from 'axios';
import Layout from '../../components/Layout';

const SingleMovie = (props) => {


    const router = useRouter();
    const { movie_id } = router.query;

    useEffect(() => {

        axios.get(`https://api.themoviedb.org/3/movie/${movie_id}?api_key=${API}&language=en-US`)
            .then(res => {
                setMovie(res.data);
            })
            .catch(err => {
                console.log(err);
                setMovie({});
            })

    })

    const [movie, setMovie] = useState({});



    return (
        <Layout>
            <div className="movie-container">
                <div className="single-movie">

                    <div className="movie-header">
                        <div>
                            <img src={movie.backdrop_path} alt="Loading Image..." />
                        </div>

                        <div>
                            <h3 className="movie-title-header">{movie.original_title}</h3>
                            <p className="runtime-p">({movie.runtime} minutes)</p>
                        </div>
                    </div>


                    <div className="movie-bottom">
                        <div>
                            <p>Overview:</p>
                            <p>{movie.overview}</p>
                        </div>


                        <div>
                            <p>Popularity: {movie.popularity}</p>
                            <p>Number of Votes: {movie.vote_count}</p>
                            <p>Rating: {movie.vote_average} <i className="fa fa-star"></i></p>
                            <p className="release-date-p">Release date: {movie.release_date.slice(8, 10) + '.' +
                                movie.release_date.slice(5, 7) + '.' + movie.release_date.slice(0, 4) + '.'}</p>
                            <p className="genres">Genres: Commedy, Drama</p>
                        </div>
                    </div>



                </div>
            </div>

        </Layout>

    )

}

export default SingleMovie;
