import { useState } from 'react';
import { API } from '../config/keys';
import Layout from '../components/Layout';
import Movies from '../components/movies/Movies';

const App = (props) => {

    const [number, setNumber] = useState(1);
    const [search, setSearch] = useState('')
    //console.log(props.popular)

    const onChange = e => {
        setSearch(e.target.value);

        if (search.length > 0) {
            App.getInitialProps = async function () {
                const res = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${API}&language=en-US&page=1&include_adult=false&query=${search}`);
                const popular = await res.json();

                return {
                    popular: popular.results
                }
                console.log(popular);
            }
        }
    }

    return (
        <Layout>
            <div>
                <h3 className="type-heading">Most Popular Movies</h3>
                <input style={{ margin: '10px' }} className="search-input" type="text" placeholder="Search" value={search} onChange={onChange} />
                <Movies movies={props.popular}></Movies>
            </div>
        </Layout>
    )
}

App.getInitialProps = async function () {
    const res = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${API}&language=en-US&page=1`);
    const popular = await res.json();

    return {
        popular: popular.results
    }
}

export default App;
