import { useState } from 'react';
import { API } from '../config/keys';

const HomePage = (props) => {

    const [number, setNumber] = useState(1);
    console.log(props.upcoming)

    return (
        <div>
            Welcome to Next.js {number}
            <button onClick={setNumber(number + 1)}>View more results</button>
        </div>
    )
}

HomePage.getInitialProps = async function () {
    const res = await fetch(`https://api.themoviedb.org/3/movie/upcoming?api_key=${API}&language=en-US&page=${number}`);
    const upcoming = await res.json();

    return {
        upcoming: upcoming.results
    }
}

export default HomePage;
